import React from "react";
import "./Dashboard.css"; // Import CSS for styling

const Dashboard = () => {
  const tips = [
    "Stay hydrated before, during, and after workouts.",
    "Proper form is more important than heavy weights.",
    "Always warm up and cool down to prevent injuries.",
    "Get enough sleep for muscle recovery.",
    "Eat a balanced diet rich in protein and carbs.",
  ];

  const exercises = [
    { bodyPart: "Chest", name: "Bench Press", img: "/images/chest.jpg" },
    { bodyPart: "Back", name: "Pull-ups", img: "/images/back.jpg" },
    { bodyPart: "Legs", name: "Squats", img: "/images/legs.jpg" },
    { bodyPart: "Arms", name: "Bicep Curls", img: "/images/arms.jpg" },
    { bodyPart: "Shoulders", name: "Overhead Press", img: "/images/shoulders.jpg" },
  ];

  return (
    <div className="dashboard">
      {/* Welcome Section */}
      <h1 className="dashboard-title">üèãÔ∏è Welcome to Your Gym Dashboard</h1>

      {/* Gym Tips Section */}
      <div className="tips-section">
        <h2>üî• Gym Tips</h2>
        <ul>
          {tips.map((tip, index) => (
            <li key={index}>‚úî {tip}</li>
          ))}
        </ul>
      </div>

      {/* Exercises Section */}
      <div className="exercises-section">
        <h2>üí™ Exercises by Body Part</h2>
        <div className="exercise-grid">
          {exercises.map((exercise, index) => (
            <div key={index} className="exercise-card">
              <img src={exercise.img} alt={exercise.name} className="exercise-img" />
              <h3>{exercise.name}</h3>
              <p>{exercise.bodyPart}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Gallery Section */}
      <div className="gallery-section">
        <h2>üì∏ Gym Gallery</h2>
        <div className="gallery-grid">
          <img src="/images/gym1.jpg" alt="Gym" />
          <img src="/images/gym2.jpg" alt="Gym" />
          <img src="/images/gym3.jpg" alt="Gym" />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
